import { motion } from "framer-motion";
import React, { FC, useEffect, useState } from "react";
import "./carousel.css"; // Import your CSS for styles

interface CarouselProps {
  children: React.ReactNode;
}

const Carousel: FC<CarouselProps> = ({ children }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const totalItems = React.Children.count(children);

  // Automatically slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % totalItems);
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval); // Cleanup on unmount
  }, [totalItems]);

  const handleNext = () => {
    setCurrentIndex((prev) => (prev + 1) % totalItems);
  };

  const handlePrev = () => {
    setCurrentIndex((prev) => (prev - 1 + totalItems) % totalItems);
  };

  return (
    <div className="carousel-container">
      <h1 className="carousel-header">CHOOSE CHARACTER</h1>
      <h2 className="carousel-subheader">AVENGERS</h2>
      <button className="arrow left" onClick={handlePrev}>❮</button>
      <motion.div
        className="carousel-items"
        initial={{ x: 0 }}
        animate={{ x: `-${currentIndex * 100}%` }}
        transition={{ duration: 0.5 }}
        style={{ display: "flex" }} // Use flex for item alignment
      >
        {React.Children.map(children, (child, index) => (
          <div className="carousel-item-wrapper" key={index}>
            {child}
          </div>
        ))}
      </motion.div>
      <button className="arrow right" onClick={handleNext}>❯</button>
    </div>
  );
};

export default Carousel;
